dbexec portability -sNe "select CONCAT('select CONCAT(CONCAT(''insert into applications (app_id,service_id,time_start,time_complete,status) VALUES ('''''',b.row_id) ,CONCAT(CONCAT('''''',',a.service_id,'  ,UNIX_TIMESTAMP(NOW()),NULL,'''''',c.request_status_name),CONCAT('''''''','');''))) NANAN from nrdbp_requests a, applications b, request_statuses c where b.request_form_id=a.np_reqform_id and a.status_rowid=c.row_id and b.app_status=''PENDING'' and a.number_from=''',LEFT(a.newvalue, 10),'''; ') from service_attributes a, service_attributes b  where a.service_id=b.service_id  and a.attribute_id=1 and b.attribute_id=12 and b.newvalue IN ('I1.2.8 - Λάθος συστήματος [AxisFault].','I1.1.16 - Απορρίφθηκε από τη hol - Λάθος Δεδομένα. Ο σταθερός αριθμός βρίσκεται ήδη σε διαδικασία Port In.');"
